package Teme;

public class Curs2Prob3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/**
		 * Printam valoarea de mijloc dintre 2 numere
		 * 20 si 45 adica tre sa printez 30
		 */

	
	int nr1 =20;
	int nr2 =45;
	
	while (nr1 == 30) {
		System.out.println(nr1++);
	}
	while (nr2 == 30) {
		System.out.println(nr2--);
	}
	}
}
